def generate_random_spectra_transform_pairs(allowed_spectrum_keys, allowed_transforms, num_pairs=10):

    spectrum_keys_list = []
    sequence_indices_list = []
    transform_keys_list = []
    max_pair_length = 10

    num_allowed_spectrum_keys = len(allowed_spectrum_keys)
    num_allowed_transforms = len(allowed_transforms)

    for pair_index in range(num_pairs):
        spectrum_keys = []
        transform_keys = []
        sequence_indices = []

        # Select a random pair length.
        pair_length = torch.randint(low=1,high=max_pair_length,size=(1,)).item()

        # Generate random spectrum key indices to pick with from allowed_spectra list.
        spectrum_key_indices = torch.randint(low=0,high=num_allowed_spectrum_keys, size=(pair_length,))

        # Generate random transform key indices to pick with from allowed_transforms list.
        transform_key_indices = torch.randint(low=0,high=num_allowed_transforms, size=(pair_length,))

        for index in range(pair_length):
            spectrum_keys.append(allowed_spectrum_keys[spectrum_key_indices[index]])
            transform_keys.append(allowed_transforms[transform_key_indices[index]])

        spectrum_keys_list.append(spectrum_keys)
        transform_keys_list.append(transform_keys)
        sequence_indices_list.append(spectrum_key_indices.tolist())

    return (spectrum_keys_list, sequence_indices_list, transform_keys_list)


